<style type="text/css" media="screen">
    /* Reset & Base Styles */
    html {
        margin: 0;
        line-height: 1.6;
        font-family: Arial, sans-serif;
        color: #1a1a1a;
    }

    body {
        margin: 0;
        background-color: #fff;
        font-size: 14px;
    }

    /* Typography */
    h1 {
        font-size: 28px;
        font-weight: bold;
        margin-bottom: 8px;
        color: #1a1a1a;
    }

    h2 {
        font-size: 24px;
        font-weight: bold;
        margin: 30px 0;
        text-transform: uppercase;
        letter-spacing: 1px;
    }

    p {
        margin: 5px 0;
        line-height: 1.5;
    }

    /* Layout Components */
    .business-header {
        text-align: center;
        margin-bottom: 40px;
    }

    .business-header p {
        margin: 3px 0;
        color: #4a4a4a;
    }

    /* Tables */
    table {
        width: 100%;
        border-collapse: collapse;
        margin: 20px 0;
    }

    th {
        background-color: #1a1a1a;
        color: #ffffff;
        text-align: left;
        padding-top: 12px;
        padding-bottom: 12px;
        font-weight: 600;
    }

    td {
        padding-top: 12px;
        padding-bottom: 12px;
        border-bottom: 1px solid #e0e0e0;
        vertical-align: top;
    }

    /* Grid Layout */
    .grid-2 {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 40px;
        margin-bottom: 30px;
    }

    /* Summary Section */
    .summary-table td {
        padding: 8px 12px;
    }

    .summary-table td:last-child {
        text-align: right;
        font-weight: 500;
    }

    .total-row td {
        font-weight: bold;
        font-size: 16px;
        border-top: 2px solid #1a1a1a;
    }

    /* Terms & Conditions */
    .terms {
        margin-top: 40px;
        font-size: 12px;
        color: #666;
    }

    .terms p {
        margin: 5px 0;
    }

    /* Utility Classes */
    .text-right {
        text-align: right;
    }

    .text-center {
        text-align: center;
    }

    .font-bold {
        font-weight: bold;
    }

    .mb-20 {
        margin-bottom: 20px;
    }

    .mt-20 {
        margin-top: 20px;
    }

    .text-sm {
        font-size: 12px;
    }

    /* Logo */
    .logo {
        max-width: 200px;
        margin: 0 auto 20px;
    }

    /* Print Specific */
    @media print {
        body {
            -webkit-print-color-adjust: exact;
            print-color-adjust: exact;
        }

        th {
            background-color: #1a1a1a !important;
            color: #ffffff !important;
        }
    }
</style>
